mapper.select:
select distinct year from fb_product_line_budget where 1=1
AND business_division = #{businessDivision}
AND product_line in
#{productLine}
order by year

select top 1 *
from fb_product_line_budget
where 1=1
AND business_division = #{businessDivision}
AND product_line = #{productLine}
order by item_seq

with temp_year_1 as ( select * from fb_product_line_budget where year = #{firstYear} and unit != '%'
AND business_division = #{businessDivision}
AND product_line in
#{productLine}
), temp_year_2 as ( select * from fb_product_line_budget where year = #{secondYear} and unit != '%'
AND business_division = #{businessDivision}
AND product_line in
#{productLine}
) select temp_year_1.item_seq itemSeq, temp_year_1.cost_item costItem, temp_year_1.unit unit ${selectColumn} from temp_year_1 left join temp_year_2 on temp_year_1.business_division = temp_year_2.business_division and temp_year_1.product_line = temp_year_2.product_line and temp_year_1.data_version = temp_year_2.data_version and temp_year_1.item_seq = temp_year_2.item_seq group by temp_year_1.item_seq, temp_year_1.cost_item, temp_year_1.unit order by temp_year_1.item_seq
