mapper.select:
SELECT A.ID, A.ORDER_NUMBER, A.MCH_CODE, A.INSPECTION_DATE, A.INSPECTION_SHIFT, A.SHIFT_START_TIME, A.SHIFT_END_TIME, A.DUTY_PERSON_ID, A.STATUS, A.CREATED_TIME, A.CREATED_BY, A.UPDATED_TIME, A.UPDATED_BY, B.MCH_NAME, B.SPEC, B.TYPE_VERSION, B.FACTORY_NO, B.EQUIP_NUMBER FROM EM_INSPECTION_ORDER A LEFT JOIN EM_EQUIPMENT B ON A.MCH_CODE = B.MCH_CODE WHERE 1=1
AND A.MCH_CODE = #{inspectionOrderQueryParam.mchCode}
AND A.STATUS = #{inspectionOrderQueryParam.status}
AND B.MCH_NAME = #{inspectionOrderQueryParam.mchName}
AND B.SPEC = #{inspectionOrderQueryParam.spec}
AND B.TYPE_VERSION = #{inspectionOrderQueryParam.typeVersion}
AND B.EQUIP_NUMBER = #{inspectionOrderQueryParam.equipNumber}
ORDER BY A.ORDER_NUMBER

SELECT A.ID, A.ORDER_NUMBER, A.MCH_CODE, A.INSPECTION_DATE, A.INSPECTION_SHIFT, A.SHIFT_START_TIME, A.SHIFT_END_TIME, A.DUTY_PERSON_ID, A.STATUS, A.CREATED_TIME, A.CREATED_BY, A.UPDATED_TIME, A.UPDATED_BY, B.MCH_NAME, B.SPEC, B.TYPE_VERSION, B.FACTORY_NO, B.EQUIP_NUMBER, C.ID ITEM_ID, C.CHECK_ITEM, C.CHECK_ITEM_STANDARD, C.MIN_VALUE, C.MAX_VALUE, C.ACTUAL_VALUE, C.CHECK_RESULT, C.IS_EXCEPTION, C.IS_FINISH, C.IS_FAULT, C.FAULT_DESC, C.IS_REPAIR, C.CREATED_TIME ITEM_CREATED_TIME, C.CREATED_BY ITEM_CREATED_BY, C.UPDATED_TIME ITEM_UPDATED_TIME, C.UPDATED_BY ITEM_UPDATED_BY FROM EM_INSPECTION_ORDER A LEFT JOIN EM_EQUIPMENT B ON A.MCH_CODE = B.MCH_CODE LEFT JOIN EM_INSPECTION_ORDER_ITEM C ON A.ID = C.INSPECTION_ORDER_ID WHERE 1=1
AND A.MCH_CODE = #{inspectionOrderQueryParam.mchCode}
AND A.STATUS = #{inspectionOrderQueryParam.status}
AND B.MCH_NAME = #{inspectionOrderQueryParam.mchName}
AND B.SPEC = #{inspectionOrderQueryParam.spec}
AND B.TYPE_VERSION = #{inspectionOrderQueryParam.typeVersion}
ORDER BY A.ORDER_NUMBER
