mapper.select:
SELECT A.ID, A.ORDER_NUMBER, A.MCH_CODE, A.MAINTENANCE_DATE, A.MAINTENANCE_PERIOD, A.PERIOD_UNIT, A.DUTY_PERSON_ID, A.STATUS, A.CREATED_TIME, A.CREATED_BY, A.UPDATED_TIME, A.UPDATED_BY, B.MCH_NAME, B.SPEC, B.TYPE_VERSION, B.FACTORY_NO, B.EQUIP_NUMBER FROM EM_MAINTENANCE_ORDER A LEFT JOIN EM_EQUIPMENT B ON A.MCH_CODE = B.MCH_CODE WHERE 1=1
AND A.MCH_CODE = #{maintenanceOrderQueryParam.mchCode}
AND A.STATUS = #{maintenanceOrderQueryParam.status}
AND B.MCH_NAME = #{maintenanceOrderQueryParam.mchName}
AND B.SPEC = #{maintenanceOrderQueryParam.spec}
AND B.TYPE_VERSION = #{maintenanceOrderQueryParam.typeVersion}
AND B.EQUIP_NUMBER = #{maintenanceOrderQueryParam.equipNumber}
ORDER BY A.ORDER_NUMBER

SELECT A.ID, A.ORDER_NUMBER, A.MCH_CODE, A.MAINTENANCE_DATE, A.MAINTENANCE_PERIOD, A.PERIOD_UNIT, A.DUTY_PERSON_ID, A.STATUS, A.CREATED_TIME, A.CREATED_BY, A.UPDATED_TIME, A.UPDATED_BY, B.MCH_NAME, B.SPEC, B.TYPE_VERSION, B.FACTORY_NO, B.EUQIP_NUMBER, C.ID ITEM_ID, C.MAINTENANCE_ITEM, C.MAINTENANCE_ITEM_STANDARD, C.MIN_VALUE, C.MAX_VALUE, C.ACTUAL_VALUE, C.MAINTENANCE_RESULT, C.IS_EXCEPTION, C.IS_FINISH, C.IS_FAULT, C.FAULT_DESC, C.IS_REPAIR, C.CREATED_TIME ITEM_CREATED_TIME, C.CREATED_BY ITEM_CREATED_BY, C.UPDATED_TIME ITEM_UPDATED_TIME, C.UPDATED_BY ITEM_UPDATED_BY FROM EM_MAINTENANCE_ORDER A LEFT JOIN EM_EQUIPMENT B ON A.MCH_CODE = B.MCH_CODE LEFT JOIN EM_MAINTENANCE_ORDER_ITEM C ON A.ID = C.MAINTENANCE_ORDER_ID WHERE 1=1
AND A.MCH_CODE = #{maintenanceOrderQueryParam.mchCode}
AND A.STATUS = #{maintenanceOrderQueryParam.status}
AND B.MCH_NAME = #{maintenanceOrderQueryParam.mchName}
AND B.SPEC = #{maintenanceOrderQueryParam.spec}
AND B.TYPE_VERSION = #{maintenanceOrderQueryParam.typeVersion}
ORDER BY A.ORDER_NUMBER
