mapper.select:
select * from ( SELECT machine_name, wafer_id, recipe_name, recipe_phase, #{paramName} as param_name, ${paramName} as param_value, plc_time, a.create_time, (ROW_NUMBER() over (partition by wafer_id order by a.id)) * 2 as plc_time_stamp -- datediff(second, min(plc_time) over (partition by wafer_id), plc_time) as plc_time_stamp FROM t_wlg_molding_machine_param_detail_data a join t_wlg_molding_machine_param_data_base b on a.serial_id = b.serial_id where machine_name = #{machineName} and wafer_id in
#{waferId}
)t order by plc_time_stamp, wafer_id

select distinct wafer_id
from t_wlg_molding_machine_param_data_base
where machine_name = #{machineName}
  and create_time between #{startTime} and #{endTime}
order by wafer_id

SELECT param_name + '_' + convert(varchar, array_id) as param_name FROM t_wlg_molding_machine_param_data a join t_wlg_molding_machine_param_data_base b on a.serial_id = b.serial_id join t_molding_machine_opc_ua_node c on a.param_id = c.id where machine_name = #{machineName} and wafer_id in
#{waferId}
group by param_name, array_id order by param_name, array_id
